import { useEffect, useState } from "react"
import React from 'react'
import {useLocation, useNavigate} from 'react-router-dom'
import {Table, Button,Row,Col } from 'react-bootstrap'
import { useDispatch, useSelector } from "react-redux"
import Loader from "../components/Loader";
import Message from "../components/Message";
import FormContainer from "../components/FormContainer";
import {Link, LinkContainer } from 'react-router-bootstrap'
import {listProducts, deleteProduct, createProduct} from '../actions/productActions'
import {PRODUCT_CREATE_RESET} from '../constants/productConstants'
import Paginate from '../components/Paginate'


function ProductListScreen() {
    const dispatch = useDispatch()

    const productList = useSelector( state => state.productList)
    const {loading, error, products, page, pages} = productList

    const productDelete = useSelector( state => state.productDelete)
    const {loading : loadingDelete, error: errorDelete, success: successDelete} = productDelete

    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} = userLogin

    const productCreate = useSelector(state => state.productCreate)
    const {loading: loadingCreate, error: errorCreate, success : successCreate, product: createdProduct} = productCreate

    let navigate = useNavigate();

    let location = useLocation()
    let keyword = location.search
    console.log('location',keyword)
    useEffect(() => {
        dispatch({type : PRODUCT_CREATE_RESET})

        if(!userInfo.isAdmin)
        {
            navigate('/login')
        }

        if(successCreate){
            navigate(`/admin/product/${createdProduct.id}/edit`)
        }else{
            dispatch(listProducts(keyword))
        }
    }, [dispatch, userInfo,successDelete, successCreate, createdProduct, keyword])


  const deleteHandler = (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
        dispatch(deleteProduct(id))
    }

  }

  const createProductHandler = () => {
    dispatch(createProduct())
  }


    return (
    <div>
        <Row className = 'align-items-center'>
            <Col>
                <h1>Products</h1>
            </Col>

            <Col className = 'text-right'>
                <Button className = 'my-3' onClick = {createProductHandler}>
                    <i className = 'fas fa-plus'></i> Create Product
                </Button>

            </Col>

        </Row>

        {loadingDelete && <Loader />}
        {errorDelete && <Message variant = 'danger'>{errorDelete}</Message>}


        {loadingCreate && <Loader />}
        {errorCreate && <Message variant = 'danger'>{errorCreate}</Message>}


        {loading
        ? (<Loader />)
        : error
            ? (<Message variant = 'danger'> {error}</Message>)
            : (
                <div>
                <Table striped bordered hover responsive className="table-sm">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>NAME</th>
                            <th>PRICE</th>
                            <th>CATEGORY</th>
                            <th>BRAND</th>
                            <th></th>
                        </tr>
                    </thead>

                    <tbody>
                        {products.map(product => (
                            <tr key = {product.id}>
                                <td>{product.id}</td>
                                <td>{product.name}</td>
                                <td>${product.price}</td>
                                <td>{product.category}</td>
                                <td>{product.brand}</td>

                                <td>
                                    <LinkContainer to = {`/admin/product/${product.id}/edit`}>
                                        <Button variant = 'light' className = 'btn-sm'> 
                                        <i className="fas fa-edit"></i>
                                        </Button>
                                    </LinkContainer>

                                    <Button variant = 'danger' className = 'btn-sm' onClick = {() => deleteHandler(product.id)}> 
                                        <i className="fas fa-trash"></i>
                                    </Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>

                </Table>
                <Paginate pages={pages} page={page}  isAdmin={true} />
                </div>
            )
        }
    </div>
  )
}

export default ProductListScreen
